css:
/* Configuration */
:root {
	--label-width: 150mm;
	--label-height: 100mm;

    --outline: 2px solid black;
    --border: 2px solid black;
}

@media screen {
	.print-format {

		width: var(--label-width);
		min-height: var(--label-height);
        page-width: 150mm;
        page-height: 100mm;
		padding: 0.0in;

        margin-left:3mm;
        margin-top:1mm;
        margin-right:1mm;
        margin-bottom:1mm;
	}
}

@page {
	size: var(--label-width) var(--label-height) landscape;
}


/* HACK: Make table using divs */
.divTable {
	display: table;
	width: 100%;
}

.divTableBody {
	display: table-row-group;
}

.divTableRow {
	display: table-row;
}

.divTableCell {
	/* border: 1px solid #999999; */
	display: table-cell;
	padding: 1px 2px;
}


/* Border classes */
.solid {
    border: var(--outline);
}

.solid-bottom {
	border-bottom: var(--border);
}

.solid-top {
	border-top: var(--border);
}

.solid-right {
	border-right: var(--border);
}

.solid-left {
	border-left: var(--border);
}
